import React, { Component } from "react";
class Home extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: []
    };
  }

  componentDidMount() {
    //   fetch("https://randomuser.me/api/?results=10")
      fetch("https://ghibliapi.herokuapp.com/films/58611129-2dbc-4a81-a72f-77ddfc1b1b49")
        .then(res => res.json())
        .then(parsedJSON => parsedJSON.results.map(data => (
          {
            id: `${data.id.name}`,
            title: `${data.name.first}`,
            description: `${data.name.last}`,
            director: `${data.location.state}, ${data.nat}`,
            producer: `${data.picture.large}`,
            release_date: ``,
            rt_score: ``

          }
        )))
        .then(items => this.setState({
          items,
          isLoaded: false
        }))
        .catch(error => console.log('parsing failed', error))
    }

    render() {
      const {items } = this.state;
        return (
          <div className="boxWhite">
            <h2>Random User</h2>
            {
              items.length > 0 ? items.map(item => {
              const {id, firstName, lastName, location, thumbnail} = item;
               return (

               <div key={id} className="bgCircle">
               <center><img src={thumbnail} alt={firstName} className="circle"/> </center><br />
               <div className="ctr">
                  {title} {lastName}<br />
                  {location}
                </div>

              </div>
              );
            }) : null
          }
          </div>
        );

    }
  }

export default Home;
