import React, { Component } from "react";
import axios from "axios";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: []
    };
  }

  componentDidMount() {
    axios.get("https://ghibliapi.herokuapp.com/films")
      .then(parsedJSON => {
        return parsedJSON.data.map(data =>(
          {
            id: `${data.id}`,
            title: `${data.title}`,
            rt_score: `${data.rt_score}`,
            director: `${data.director}`,
            producer: `${data.producer}`,
            release_date: `${data.release_date}`,
            description: `${data.description}`
          }
        ))
      })
      .then(items => this.setState({
        items: [
          ...this.state.items.slice(0, 0),
          ...items,
        ],
        isLoaded: false
      }))
      .catch(error => console.log('parsing failed', error))
  }

  render() {
    const { items } = this.state;

    let eachNames = title.split(' ').map(n => n.trim()).filter(n => n.length > 0);
    let initial = '';
    if(eachNames.length == 1){
        initial = eachNames[0].substr(0,2);
    }else if (eachNames.length > 1){
        initial = eachNames.slice(0,2).map(name =>name[0]).join('');
    }

    return (
      <div className="boxWhite">
        {
          (items || []).length > 0 ? items.map(item => {
            const { id, title, rt_score, director, producer, release_date, description } = item;
            return (
              <div key={id} className="boxItem">
                <div className="boxItem__thumb">
                  <img src="https://via.placeholder.com/200x250"/>
                  {initial.toUpperCase()}
                </div>
                <div className="boxItem__description">
                  <h3>{title} <span className="rating">{rt_score}</span></h3>
                  <div className="boxItem__description__detail">
                    <table>
                      <tr>
                        <td><p>Director</p></td>
                        <td><p>:</p></td>
                        <td><p>{director}</p></td>
                      </tr>
                      <tr>
                        <td><p>Producer</p></td>
                        <td><p>:</p></td>
                        <td><p>{producer}</p></td>
                      </tr>
                      <tr>
                        <td><p>Release</p></td>
                        <td><p>:</p></td>
                        <td><p>{release_date}</p></td>
                      </tr>
                    </table>
                    <dic className="desc">
                      <h4>Sinopsis</h4>
                      <p>{description}</p>
                    </dic>
                  </div>
                </div>
              </div>
            );
          }) : null
        }
      </div>
    );

  }
}
