import React, { Component } from "react";
import axios from "axios";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: []
    };
  }

  componentDidMount() {
    axios.get("https://ghibliapi.herokuapp.com/films")
      .then(parsedJSON => {
        return parsedJSON.data.map(data =>(
          {
            id: `${data.id}`,
            title: `${data.title}`,
            description: `${data.description}`
          }
        ))
      })
      .then(items => this.setState({
        items: [
          ...this.state.items.slice(0, 0),
          ...items,
        ],
        isLoaded: false
      }))
      .catch(error => console.log('parsing failed', error))
  }

  render() {
    const { items } = this.state;
    return (
      <div className="boxWhite">
        {
          (items || []).length > 0 ? items.map(item => {
            const { id, title, description } = item;
            return (
              <div key={id} className="boxItem">
                <div className="boxItem__thumb">
                  <a href="https://placeholder.com"><img src="https://via.placeholder.com/200x300"/></a>
                </div>
                <div className="boxItem__description">
                  <h3>{title} <span className="rating">{rt_score}</span></h3>
                  <div className="boxItem__description__detail">
                    <table>
                      <tr>
                        <td><p>Director</p></td>
                        <td>:</td>
                        <td><p>{director}</p></td>
                      </tr>
                      <tr>
                        <td><p>Producer</p></td>
                        <td>:</td>
                        <td><p>{producer}</p></td>
                      </tr>
                      <tr>
                        <td><p>Release</p></td>
                        <td>:</td>
                        <td><p>{release_date}</p></td>
                      </tr>
                    </table>
                    <p>{description}</p>
                  </div>
                </div>
              </div>
            );
          }) : null
        }
      </div>
    );

  }
}
